import React, { Component } from 'react';
import Button from "react-bootstrap/Button";
import SearchIcon from "@material-ui/icons/Search";
import {
    Link
} from "react-router-dom";

class Searchbar extends Component {

    constructor(props) {
        super(props);
        this.state = {
            initialState: "Search for businesses in Indianapolis...",
            currentText: ""
        }
    }

    style = {
        height: 40,
        fontSize: 16,
        width: 600
    };

    changeText(currentText) {
        this.setState({currentText});
        //console.log({currentText});
    }

    render() {
        return (
            <form>
                <div className="flexbox-container">
                    <input style={this.style} type="text" className="custom-control-inline form-control"
                           placeholder=" Search for a local business in Indianapolis..."/>
                    <Link to={{
                        pathname: "/getBusinesses/" + this.state.currentText,
                        state: {
                            currentText: this.state.currentText
                        }
                    }}>
                        <Button type="submit" onClick={this.changeText.bind(this, 'currentText')}>
                            <SearchIcon />
                        </Button>
                    </Link>
                </div>
            </form>
        );
    }

}

export default Searchbar;
