import React, { Component } from 'react';
import Button from "react-bootstrap/Button";

class Searchbar extends Component {

    constructor(props) {
        super(props);
        this.state = {
            initialState: "Search for businesses in Indianapolis...",
            currentText: " "
        }
    }

    style = {
        height:200,
        fontSize: 18,
        width: 600
    };

    changeText(currentText) {
        this.setState({currentText});
        //console.log({currentText});
    }

    render() {
        return (
            <form>
                <div className="search-box">
                    <input style={this.style} type=" text" className="custom-control-inline"
                           placeholder=" Search for a local business in Indianapolis..."/>
                    <Button onClick={this.changeText.bind(this, 'currentText')}>Search</Button>
                </div>
            </form>
        );
    }

}

export default Searchbar;
