import React, { Component } from 'react';
import { Button, Form } from "react-bootstrap";

class Searchbar extends Component {

    constructor(props) {
        super(props);
        this.state = {
            initialState: "Search for businesses in Indianapolis...",
            currentText: " "
        }
    }

    changeText(currentText) {
        this.setState({currentText});
        //console.log({currentText});
    }

    render() {
        return (
            // <div class="search-box">
            //     <Form>
            //         <input type="text" placeholder={this.state.initialState} onChange={this.changeText.bind(this, 'currentText')} />
            //     </Form>
            //
            // </div>
            <form>
                <div className="search-box">
                    <input size={90} type="text" className="custom-control-inline"
                           placeholder=" Search for a local business in Indianapolis..."/>
                    <Button onClick={this.changeText.bind(this, ' currentText')}>Search</Button>
                </div>
            </form>
        );
    }

}

export default Searchbar;
